package security

import org.scalatest.WordSpec
import javax.inject.Inject
import javax.inject.Singleton
import stubs.Stubs
import play.api.libs.iteratee._
import scala.concurrent.duration._
import scala.concurrent.Await
import scala.concurrent.ExecutionContext.Implicits.global
import org.apache.commons.codec.binary.Hex
import org.scalatest.MustMatchers

class CryptoServiceTest extends WordSpec with MustMatchers {

  "CryptoService" must {
    "encrypt and decript some text" in {

      val plainTxt = "".getBytes()
      val cryptoService = new CryptoServiceImpl(0)
      val cipherTxt = cryptoService.encrypt(plainTxt, Stubs.authPair)
      val result = cryptoService.decrypt(cipherTxt, Stubs.authPair)

      result mustBe plainTxt
    }

  }

  "CryptoService" must {
    "encrypt and empty array without failling" in {

      val plainTxt = Array.emptyByteArray
      val cryptoService = new CryptoServiceImpl(0)
      val cipherTxt = cryptoService.encrypt(plainTxt, Stubs.authPair)
      val result = cryptoService.decrypt(cipherTxt, Stubs.authPair)

      result mustBe plainTxt
    }

  }

  "CryptoService" must {
    "create a encrypt Enumeratee" in {

      val cryptoService = new CryptoServiceImpl(0)

      val plainTxt = "olaholaholaholaholaholaholahol".getBytes()

      val plainSource = plainTxt.grouped(7).foldLeft(Enumerator.empty[Array[Byte]]) {
        case (enumerator, chunk) =>
          enumerator >>> Enumerator(chunk)
      }
      val encryptEnumeratee = cryptoService.encryptEnumeratee(Stubs.authPair)
      val cipherPromise = Iteratee.flatten(plainSource |>> encryptEnumeratee &>> Iteratee.consume[Array[Byte]]()).run
      val cipherBytes = Await.result(cipherPromise, Duration(3, SECONDS))

      val cipherSource = cipherBytes.grouped(7).foldLeft(Enumerator.empty[Array[Byte]]) {
        case (enumerator, chunk) =>
          enumerator >>> Enumerator(chunk)
      }
      val decryptEnumeratee = cryptoService.decryptEnumeratee(Stubs.authPair)
      val plainPromise = Iteratee.flatten(cipherSource |>> decryptEnumeratee &>> Iteratee.consume[Array[Byte]]()).run
      val plainBytes = Await.result(plainPromise, Duration(3, SECONDS))

      plainBytes mustBe plainTxt
    }
  }
}